import React from "react";
import { Col, Container, Row } from "react-bootstrap";

class SingleArtist extends React.Component {
  state = {
    album: {},
    tracks: [],
    loading: true,
  };

  fetchArtist = async (id) => {
    try {
      const url = "https://deezerdevs-deezer.p.rapidapi.com/album/";
      const response = await fetch(url + id, {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "1e45b6f96fmshc79e842018c9ea0p1288b9jsn2ba5d9842c9f",
          "x-rapidapi-host": "deezerdevs-deezer.p.rapidapi.com",
        },
      });
      const responseObject = await response.json();

      this.setState({
        album: responseObject,
        tracks: responseObject.tracks.data,
        loading: false,
      });
    } catch (error) {
      console.log(error);
    }
  };
  async componentDidMount() {
    this.fetchArtist(this.props.match.params.id);
  }
  render() {
    const { album, loading, tracks } = this.state;
    return (
      <>
        <Container>
          <Row>
            <Col>
              {!loading && (
                <div className="album-page">
                  <img
                    style={{
                      marginTop: 30,
                      marginLeft: 100,
                      width: 400,
                      height: 400,
                    }}
                    src={album.cover_big}
                    alt=""
                  />

                  <div className="track-list ml-5">
                    <h2
                      style={{
                        color: "white",
                        marginBottom: 30,
                        marginLeft: 100,
                        marginTop: 5,
                      }}
                    >
                      {album.title}
                    </h2>
                    <ul>
                      {tracks.map((track) => (
                        <li className="d-flex justify-content-between">
                          {track.title} <span>{track.duration}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default SingleArtist;
