import React from "react";

class SingleArtist extends React.Component {
  state = {
    album: {},
    tracks: [],
    loading: true,
  };

  componentDidMount(id) {
    fetch("https://https://deezerdevs-deezer.p.rapidapi.com/album/" + id, {
      method: "GET",
      headers: {
        "x-rapidapi-key": "1e45b6f96fmshc79e842018c9ea0p1288b9jsn2ba5d9842c9f",
        "x-rapidapi-host": "deezerdevs-deezer.p.rapidapi.com",
      },
    })
      .then((response) => response.json())
      .then((responseObject) =>
        this.setState({
          album: responseObject,
          tracks: responseObject.tracks.data,
          loading: false,
        })
      )
      // .then(() => this.setState({ loading: false }))
      .catch((err) => {
        this.setState({ error: true });
        console.log("An error has occurred:", err);
      });
  }

  render() {
    const { album, loading, tracks } = this.state;
    return (
      <div>
        <>
          {!loading && (
            <div className="album-page">
              <img
                style={{ marginLeft: 100, width: 400, height: 400 }}
                src={album.cover_big}
                alt=""
              />

              <div className="track-list ml-5">
                <h2 style={{ color: "white", marginBottom: 30 }}>
                  {album.title}
                </h2>
                <ul>
                  {tracks.map((track) => (
                    <li className="d-flex justify-content-between">
                      {track.title} <span>{track.duration}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          )}
          {loading && <h1>Loading...</h1>}
        </>
      </div>
    );
  }
}

export default SingleArtist;
